import React, { useState } from 'react'

import {Link} from ""
import { blogsData } from '../data'

const Blogs = () => {
  
    const [blogs,setBlogs] = useState(blogsData);
    console.log(blogs);
    const truncateString = (str,num) => {
      if(str.length > num){
        return str.slice(0,num);
      }else
      return str;
    } 
   
  return (
    <div>
        <h1 className='h1' >Welcome to Blog Page</h1>
        <h2 className='h2'>You are logged in site, So you can read it!</h2>
        <section>
          {blogs.map((blog)=>{
            const {id, title, body} = blog;
            return(
              <article key={id}>
                <h1> {title} </h1>
                <p> {truncateString(body,100)} </p>
                <Link to={title}>Learn More...</Link>
              </article>
            )
          })}
        </section>
    </div>
  )
}

export default Blogs
